sp_header_footer.client_script:
function ($rootScope, spModal, $scope, snRecordWatcher, spUtil, $location, $uibModal, cabrillo, $timeout, $window, $http) {
	var c = this;

	c.work_tables = [];
	c.job_title = "";
	c.tasks = [];
	c.approvals = [];
	c.requests = [];
	c.reports = [];
	c.assigned_to_string = "";

	updateUserTasks();

	//API CALLS FOR RECORDS

	//Gets job title with logged in user sys_id
	$http.get('/api/now/table/sys_user?sysparm_query=sys_id%3D' + $scope.user.sys_id).then(function(response){
		c.job_title = response.data.result[0].u_jt_string;
		c.work_tables.push(response.data.result[0].u_work_tables);
	});

	//Gets scrum tasks of logged in user
	function updateUserTasks()
	{
		$http.get('/api/now/table/rm_scrum_task?sysparm_query=assigned_to%3D' + $scope.user.sys_id + '%5Eactive%3Dtrue').then(function(response){
		for(var i = 0; i < response.data.result.length; i++)
		{
			c.tasks.push({number: response.data.result[i].number, short_description: response.data.result[i].short_description, sys_id: response.data.result[i].sys_id, table: response.data.result[i].sys_class_name});
		}
	});
	}



	//Gets incidents of logged in user

	$http.get('/api/now/table/incident?sysparm_query=assigned_to%3D' + $scope.user.sys_id + '%5Eactive%3Dtrue%5EstateNOT%20IN6%2C7').then(function(response){
		for(var i = 0; i < response.data.result.length; i++)
		{
			c.tasks.push({number: response.data.result[i].number, short_description: response.data.result[i].short_description, sys_id: response.data.result[i].sys_id, table: 'incident'});
		}
	});

	//Gets sc_tasks of logged in user
	$http.get('/api/now/table/sc_task?sysparm_query=assigned_to%3D' + $scope.user.sys_id + '%5Eactive%3Dtrue%5EstateNOT%20IN3%2C4').then(function(response){
		for(var i = 0; i < response.data.result.length; i++)
		{
			c.tasks.push({number: response.data.result[i].number, short_description: response.data.result[i].short_description, sys_id: response.data.result[i].sys_id, table: 'sc_task'});
		}
	});


	//Get approvals for logged in user

	$http.get('/api/now/table/sysapproval_approver?sysparm_query=approver%3D' + $scope.user.sys_id + '%5Estate%3Drequested').then(function(response){
		for(var i=0; i < response.data.result.length; i++)
			{
				c.approvals.push({sys_id: response.data.result[i].sys_id, for_user: response.data.result[i].sys_created_by, table: 'sysapproval_approver', approval_for: response.data.result[i].u_approval_for_string});
			}
	});


	//Gets open service catalog request for user
	$http.get('/api/now/table/sc_req_item?sysparm_query=u_requested_for%3D' + $scope.user.sys_id + '%5Eactive%3Dtrue').then(function(response){ 
		for(var i=0; i < response.data.result.length; i++)
			{
				c.requests.push({number: response.data.result[i].number, short_description: response.data.result[i].short_description, sys_id: response.data.result[i].sys_id, table: 'sc_req_item'});
			}
	});

	//Get user reports
	$http.get('/api/now/table/sys_report?sysparm_query=user%3D' + $scope.user.sys_id).then(function(response){
		for(var i=0; i < response.data.result.length; i++)
			{
				c.reports.push({name: response.data.result[i].sys_name, sys_id: response.data.result[i].sys_id, table: 'sys_report_template',
											  url: 'https://progleasing.service-now.com/nav_to.do?uri=%2Fsys_report_template.do%3Fjvar_report_id%3D' + 
																		response.data.result[i].sys_id + '%26jvar_selected_tab%3DallReports%26jvar_list_order_by%3D%26jvar_list_sort_direction%3D%26sysparm_reportquery%3D%26jvar_search_created_by%3D%26jvar_search_table%3D%26jvar_search_report_sys_id%3D%26jvar_report_home_query%3D'});
			}
	});


	//RECORD AND BUTTON FUNCTIONS

	c.updateApproval = function(approvals)
	{
		spModal.open({
			widget: 'portal_form',
			size: 'lg',
			widgetInput: {sys_id: approvals.sys_id, table: approvals.table}
		});
	}

	c.updateTask = function(task)
	{
			spModal.open({
			widget: 'portal_form',
			size: 'lg',
			widgetInput: {sys_id: task.sys_id, table: task.table}
		});	
	}	

	c.updateRequest = function(requests)
	{
			spModal.open({
			widget: 'portal_form',
			size: 'lg',
			widgetInput: {sys_id: requests.sys_id, table: requests.table}
		});	
	}	



	//RECORD WATCHERS
	c.updatedFromServer = false;

	spUtil.recordWatch($scope, 'rm_scrum_task', 'assigned_to=' + $scope.user.sys_id, function(updateObj){
		spUtil.update($scope);
		c.updatedFromServer = true;
		console.log("RECORD WATCHER STARTED");
		updateUserTasks();
		console.log(updateObj);
	});

}

