sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */


	if(!input){


		data.summary = [];
		data.leaders_lounge = options.leaders_lounge;
		data.url_test = "www.google.com";

		if(data.leaders_lounge == 'true'){
			var ll = new GlideRecord('kb_knowledge');
			ll.addEncodedQuery('sys_class_name!=^active=true^kb_knowledge_base=7f4ff8eddf403100b5157a0d3df2631e^can_read_user_criteria=7213507e1319e704836075e36144b078');
			ll.query();

			while(ll.next()){
				data.summary.push({
					name: ll.short_description.getDisplayValue(),
					url: "https://progleasing.service-now.com/kb_view.do?sys_kb_id=" + ll.sys_id.toString()
				})
			}
		}
		else{

		var gr = new GlideRecord('x_prole_intranet_popular_content');
		gr.addQuery('u_active', true);
		gr.query();


		while(gr.next())
		{
				data.summary.push({
				name:gr.u_name.getDisplayValue() || 'None',
				url:gr.u_url.toString()
			});
		}
		console.log(data.summary);
	}
	}

})();
