sp_widget.script:
(function(spModal) {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var user = gs.getUserID();
	var location;

	var c = this;

	if(!input){
		data.title = options.title.toUpperCase();
		data.summary = [];
		data.limit = options.limit;
		data.table = 'x_prole_intranet_announcements';
		data.department = options.department;
		data.groupBy = 'u_heading';
		data.fields = 'u_description';

		var gr_user = new GlideRecord('sys_user');
		gr_user.addQuery('sys_id', user);
		gr_user.query();
		while(gr_user.next())
			{
				location = gr_user.location.state.getDisplayValue();
				//console.log(location);
			}		

			if(location == 'AZ'){ //AZ conversion
			location = 'Arizona';
		}

		var ga = new GlideRecord(data.table);

		ga.orderByDesc('number');
		ga.addQuery('u_active', true);
		if(location != ''){
			ga.addQuery('u_location', location);
		}

		ga.addQuery('u_department', data.department);
		ga.setLimit(data.limit);
		ga.query();



		//console.log(data.department + ', ' + ga.getRowCount() + ', ' + location);

		while(ga.next())
		{
			data.summary.push({
				agName:ga[data.groupBy].getDisplayValue() || 'None',
				agValue:ga[data.groupBy].toString(),
				agSys_id: ga.sys_id,
				agContent: ga.u_content.getDisplayValue()
			});
		}
	}

})();
