sp_widget.client_script:
function($scope, spModal) {
  /* widget controller */
  var c = this;

  $scope.myInterval = c.options.interval;
  $scope.noWrapSlides = false;
  $scope.active = 0;
  var slides = $scope.slides = [];
  var currIndex = 0;

  $scope.addSlide = function() {
    var newWidth = 600 + slides.length + 1;
    slides.push({

      image: 'https://picsum.photos/' + newWidth + '/500',
      text: ['Nice image','Awesome photograph','That is so cool','I love that'][slides.length % 4],
      id: currIndex++
    });
  };
	//console.log(slides)

  $scope.randomize = function() {
    var indexes = generateIndexesArray();
    assignNewIndexesToSlides(indexes);

  };

  for (var i = 0; i < c.data.att_count; i++) {
    $scope.addSlide();
  }

  // Randomize logic below

  function assignNewIndexesToSlides(indexes) {
    for (var i = 0, l = slides.length; i < l; i++) {
      slides[i].id = indexes.pop();
    }
  }

  function generateIndexesArray() {
    var indexes = [];
    for (var i = 0; i < currIndex; ++i) {
      indexes[i] = i;
    }
    return shuffle(indexes);
  }


c.editContent = function(){

	var widgetInput = {
			table: $scope.data.table,
			sys_id: c.data.sys_id
		};


		spModal.open({
			size: "lg",
			title: "Edit",
			widget: 'intranet_attachment_clone',
			shared: c.data,
			widgetInput: widgetInput


			}).then(function(){
				//console.log($scope);
			c.server.update();

		});


	}


  // http://stackoverflow.com/questions/962802#962890
  function shuffle(array) {
    var tmp, current, top = array.length;

    if (top) {
      while (--top) {
        current = Math.floor(Math.random() * (top + 1));
        tmp = array[current];
        array[current] = array[top];
        array[top] = tmp;
      }
    }

    return array;
  }



}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	var image = [];
	var nonactive = [];
  data.sys_id = options.record_sys_id    // || options.record_id || $sp.getParameter("sys_id");
	data.table =  'u_service_portal_intranet'
	data.image = '';
	data.att_count = '' ;

	var grdb = new GlideRecord('sys_attachment');


	grdb.addQuery('table_sys_id' , data.sys_id);
	grdb.orderBy('sys_created_on');
	grdb.query();
data.att_count = grdb.getRowCount() 

while (grdb.next()){
		//console.log('found')
		image.push('sys_attachment.do?sys_id='+ grdb.sys_id);
	}



	data.image = image;
	//console.log(data.image)

})();
