sp_widget.client_script:
function($scope, spModal) {
  /* widget controller */
  var c = this;

	var page = $scope.page.sys_name;	

	submit_ticket = c.options.submit_ticket_button;

	console.log(c);

	c.editContent = function(){

			var widgetInput = {
			table: $scope.data.table,
			sys_id: c.data.sys_id
		};

		spModal.open({
			size: "lg",
			title: "Edit",
			widget: 'intranet_attachment_clone',
			shared: c.data,
			widgetInput: widgetInput,

			buttons: [
				{
					label: "Submit",
					primary: true
				}
			]
			}).then(function(){

			c.server.update();

		});

	}







}

