sp_widget.script:
(function(spModal) {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	var c = this;

	if(!input){
		data.summary = [];
		data.table = 'x_prole_intranet_announcements';
		data.groupBy = 'u_heading';
		data.fields = 'u_description';
		var ga = new GlideAggregate(data.table);
		ga.setGroup(true);
		ga.groupBy(data.groupBy);
		ga.addAggregate('COUNT');
		ga.query();

		while(ga.next())
		{
			data.summary.push({
				agName:ga[data.groupBy].getDisplayValue() || 'None',
				agValue:ga[data.groupBy].toString(),
				agCount:ga.getAggregate('COUNT')
			});
		}
	}

})();
